void animation_run(void) {
	if(timer_elapsed32(anim_sleep) > OLED_TIMEOUT) {
		oled_off();
	} else {
		if(timer_elapsed32(anim_timer) > LAYER_FRAME_DURATION) {
			anim_timer = timer_read32();
			anim_phase = true;
			}
		}
}

